function findLongestWordLength(str) {

  let currentlength=0;
  let maxlength=0;

for (let i=0; i<str.length;i++){
  if (str[i] === " "){
if(currentlength>maxlength){
maxlength=currentlength;
}
currentlength=0;

  } else{
    currentlength++;

  }
}
if (currentlength > maxlength) {
  maxlength = currentlength;
}

  return maxlength;
}
