function mutation(arr) {
  // Convert both strings to lowercase
  let str1 = arr[0].toLowerCase();
  let str2 = arr[1].toLowerCase();

  // Check if each character in str2 is present in str1
  for (let char of str2) {
    if (str1.indexOf(char) === -1) {
      return false;
    }
  }

  return true;
}
